INSERT INTO films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) VALUES
('300 Спартанцев', 'Рассказ о кровопролитной битве при Фермопилах в 480 году до н. э., в которой триста отважных спартанцев во главе с царем Леонидом преградили путь многотысячной армии персидского царя Ксеркса.', 2007, 6, 16, 117, 3),
('1408', 'Известный писатель Майк Энслин, автор хоррор-романов, пишет очередную книгу о необычных явлениях и полтергейсте в отелях. Не веря в существование загробной жизни, Энслин решает поселиться в печально известном номере 1408 отеля «Дельфин».', 2007, 3, 16, 104, 4),
('Гарри Поттер и Орден Феникса', 'Гарри проводит свой пятый год в школе Хогвартс и обнаруживает, что многие из членов волшебного сообщества отрицают факт недавнего состязания юного волшебника с воплощением вселенского зла Волдемортом.', 2007, 10, 12, 138, 5),
('Я - Легенда', 'Неизвестный вирус унёс жизни половины населения земного шара, а остальную половину превратил в вампиров. Единственный уцелевший человек с иммунитетом к заболеванию ночами держит осаду упырей, а днем пытается найти противоядие и докопаться до причин эпидемии.', 2007, 4, 16, 96, 6),
('Мгла', 'Маленький городок накрывает сверхъестественный туман, отрезая людей от внешнего мира. Группе героев, оказавшихся в этот момент в супермаркете, приходится вступить в неравный бой с обитающими в тумане монстрами.', 2007, 3, 18, 126, 7),
('Старикам тут не место', 'Обычный работяга обнаруживает в пустыне гору трупов, набитый героином грузовик и соблазнительную сумму в два миллиона долларов наличными. Он решает взять деньги себе, и результатом становится волна насилия, которую не может остановить вся полиция Западного Техаса.', 2007, 5, 16, 122, 8),
('Человек-Паук 3: Враг в отражении', 'Когда Человек-паук надевает таинственный черный костюм, его темная, более мстительная сторона полностью поглощает его. Эти трансформации заставляют его встретиться с самым сильным противником ― с самим собой…', 2007, 4, 12, 139, 9);